import React from 'react';
import CartItem from './CartItem';
import './index.css';
class Cart extends React.Component{
    constructor(){
        super();
        this.state={
            products:[
                {
                title:"Phone",
                price:16500,
                qty:1,
                img:'',
                id:1
                },
                {
                title:"Watch",
                price:1750,
                qty:1,
                img:'',
                id:2
                },
                {
                title:"Laptop",
                price:40000,
                qty:1,
                img:'',
                id:3
                }
            ]
        }
        // this.increaseQuantity=this.increaseQuantity.bind(this);
        // this.testing();
    }
    handleIncreaseQuantity=(product)=>{
       console.log(product);
       const{products}=this.state;
       const index=products.indexOf(product);
       this.setState({
        products:products
       })
    }
           render(){
            const{products}=this.state;
            return(
                <div className='cart'>
                    {
                        products.map((product)=>{
                            return(
                                <CartItem 
                                product={product}
                                 key={product.id}
                                 onIncreaseQuantity={this.handleIncreaseQuantity}
                                 />
                            )
                            
                        })
                    }
                   
                </div>
            )
           }
        
    }


export default Cart;
