import React from 'react';

class CartItem extends React.Component{
    constructor(){
        super();
        this.state={
            title:"Phone",
            price:9999,
            qty:1,
            img:''
        }
    }
    render(){
        return(
            <div className='cart-item '>
             <div className='left-block'>
                <img style={styles.image}/>
             </div>

             <div className='right-block'>
                <div style={{fontSize:25}}>Phone</div>
                <div style={{color:'#777'}}>Rs 9999</div>
                <div style={{color:'#777'}}>Qty:1</div>
                <div className='cart-item-actions'>
                    {/*Buttons*/}
                    <img alt='increase' className='action-icons' src='https://cdn-icons-png.flaticon.com/128/8727/8727642.png'/>
                    <img alt='decrease' className='action-icons' src='https://cdn-icons-png.flaticon.com/128/8727/8727682.png'/>
                    <img alt='delete' className='action-icons' src='https://cdn-icons-png.flaticon.com/128/6096/6096937.png'/>
                </div>
             </div>




            </div>
        )
    }
}

const styles={
    image:{
        height:110,
        width:110,
        borderRadius:4,
        background:'#ccc'
    }
}

export default CartItem;
